package b_operator;

public class Operator_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean bCompare1 = "문자"=="문자";
		boolean bCompare2 = 1>5;
		boolean bCompare3 = 3>=3;
		boolean bCompare4 = "문자"!="숫자";

		System.out.println("문자==문자 : " + bCompare1);
		System.out.println("1>5 : " + bCompare2);
		System.out.println("3>=3 : " + bCompare3);
		System.out.println("문자!=숫자 : " + bCompare4);

	}

}




