package c_conditional;

public class Conditional_4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int iNum = 10;

		if (iNum > 5) {
			System.out.println("5보다 큽니다.");
		}
		else if (iNum == 5) {
			System.out.println("5와 같습니다.");
		}
		else {
			System.out.println("5보다 작습니다.");
		}

	}

}


