class Getset{
	private int MethodA;
	private int MethodB;
	private int MethodC;
	public int getMethodA() {
		return MethodA;
	}
	public void setMethodA(int methodA) {
		MethodA = methodA;
	}
	public int getMethodB() {
		return MethodB;
	}
	public void setMethodB(int methodB) {
		MethodB = methodB;
	}
	public int getMethodC() {
		return MethodC;
	}
	public void setMethodC(int methodC) {
		MethodC = methodC;
	}
}
