package q_practice_ex;

public class Ex_6_2_for_while_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 아래 for문을 while문으로 바꿔보세요.
		 */
		System.out.println("for문");
		for(int i=1;i<=100;i++)
		{
			if(i%2==0)
			{
				System.out.print(i + " ");
			}
		}
	}
}
