package r_practice_ans;

public class Ex_6_2_for_4_ans {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 연습문제 for_4) 도형 출력(1)
		 * 
		 * 		***
		 *  	***
		 *  	***
		 *  	***
		 *  
		 */

		for(int i=0;i<4;i++){
			for(int j=0;j<3;j++){
				System.out.print("*");
			}
			System.out.println();
		}
	}

}
