package r_practice_ans;

public class Ex_6_2_while_2_ans {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * 연습문제 while_2)
		 *
		 * if문을 사용하지 않고 1 ~ 100까지 정수 중  2의 배수 출력
		 * 
		 */
		int iNum=2;

		while(iNum<=100)
		{
			System.out.println(iNum);
			iNum = iNum+2;
		}
	}

}
